%cassandra

INSERT INTO spark_demo.performers(name,country,styles) VALUES('Patrick McFadin','USA',['Evangelism','Technical Gospel']);
SELECT name,country,styles FROM spark_demo.performers WHERE name='Patrick McFadin';

%cassandra
# Passing runtime consistency with @consistency=xxx
@consistency=THREE
SELECT * FROM spark_demo.performers LIMIT 5;

%cassandra

#Timestamp in the past
@timestamp=10
CREATE TABLE IF NOT EXISTS spark_demo.ts(key int PRIMARY KEY, value text);
TRUNCATE spark_demo.ts;

#Force timestamp directly in the first INSERT
INSERT INTO spark_demo.ts(key,value) VALUES(1,'val1') USING TIMESTAMP 100;

#Select some data to loose some time
SELECT * FROM spark_demo.albums LIMIT 100;

#Use @timestamp value set at the beginning(10)
INSERT INTO spark_demo.ts(key,value) VALUES(1,'val2') ;

#Check the result
SELECT * FROM spark_demo.ts WHERE key=1;

%cassandra

@prepare[select_10_artists]=SELECT name,country,styles FROM spark_demo.performers LIMIT 10
@prepare[select_artist_by_name]=SELECT name,country,styles FROM spark_demo.performers WHERE name=:name

%cassandra

@bind[select_artist_by_name]='The Beatles'
#@bind[select_10_artists]

%cassandra

#Removing an unknow statement should has no side effect
@remove_prepare[unknow_statement]
@remove_prepare[select_artist_by_name]

#This should fail because the 'select_artist_by_name' has been removed
@bind[select_artist_by_name]='The Beatles'

%cassandra
#Having a block of statement with missing semi-colon is always an error
USE spark_demo;
SELECT * FROM albums LIMIT 10
SELECT name,country,styles FROM performers LIMIT 10;

%cassandra

#A bind predicate with semi-colon in the middle of a batch is surely an error

@prepare[insert_test_album]=INSERT INTO test_album(artist,title) VALUES(?,?)

BEGIN UNLOGGED BATCH
    @bind[insert_test_album]='him','mini him';
APPLY BATCH;

%cassandra
USE spark_demo;
DESCRIBE table performers;

%cassandra

CREATE TYPE IF NOT EXISTS spark_demo.address(
    number int,
    street text,
    zip int,
    city text,
    country text
);

DESC TYPE spark_demo.address;
